import React, { useState, setState, useEffect } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import ToggleMenu from './ToggleMenu';
import { Button, Box } from '@material-ui/core';
import { Link } from "react-router-dom";
import menu from '../globals';
import { logout } from './tools/ApiCalls';
import { useStore } from 'react-hookstore';

const useStyles = makeStyles(theme => ({
  grow: {
    flexGrow: 1,
  },
  title: {
    display: 'none',
    margin: theme.spacing(1),
    [theme.breakpoints.up('sm')]: {
      display: 'block',
    },
  },
  iconContainer: {
    display: 'flex',
  },
  menuBar: {
    marginRight: '50px',
  },
  signInButton: {
    margin: theme.spacing(1),
  },
  inputRoot: {
    color: 'inherit',
  },
  sectionDesktop: {
    display: 'none',
    [theme.breakpoints.up('md')]: {
      display: 'flex',
    },
  },
  sectionMobile: {
    display: 'flex',
    [theme.breakpoints.up('md')]: {
      display: 'none',
    },
  },
  searchField: {
    width: '100%',
  },
  backButton: {
    margin: theme.spacing(1),
  }
}));

function AuthMenuBar(props) {
  const classes = useStyles();
  const handleClick = () => {
    logout();
    props.setAuth(false)
    console.log(localStorage.getItem('isLoggedIn'))

  }

  return (
    <div style={{ display: 'flex' }}>
      <Button variant="contained" color="secondary" className={classes.signUpButton} component={Link} to="/" onClick={handleClick}>
        DÃ©connexion
      </Button>
    </div>
  )
}

function BaseNavBar(props) {
  const classes = useStyles();
  const [ auth, setAuth ] = useStore('authStore');

  useEffect(() => {
    console.log(auth)
  });

  return (
    <Toolbar >
      <div className={classes.sectionMobile}>
        <ToggleMenu side='left' menu={menu} />
      </div>
      <Typography className={classes.title} variant="h6" noWrap>
        Thirty One
    </Typography>
      <div className={classes.sectionDesktop}>
        <div className={classes.menuBar}>
          {menu.map((item) => (
            ((auth && !item.public) || item.public) && <Button color="inherit" component={Link} to={item.route}>{item.title}</Button>
          ))}
        </div>
      </div>
      <div className={classes.grow} />
      <div className={classes.iconContainer}>
        {auth ? <AuthMenuBar setAuth={setAuth} /> : <NonAuthMenuBar />}
      </div>
    </Toolbar>
  );
}

export default function NavBar(props) {
  const classes = useStyles();
  const [searchActive, setSearchActive] = useState(false)
  return (
    <div>
      <AppBar position={!props.sticky ? "static" : 'fixed'}>
        <BaseNavBar />
      </AppBar>
      {props.sticky && <Box height="64px" />}
    </div>
  );
}
