import React, { Component, useState } from 'react';
import { request_token, request_pages } from '../tools/ApiCalls';
import { makeStyles } from '@material-ui/core/styles';
import { withStyles } from '@material-ui/core/styles';
import InfiniteScroll from "react-infinite-scroller";
import Container from '@material-ui/core/Container';
import Spinner from '../Spinner'
import Grid from '@material-ui/core/Grid';
import Dialog from '@material-ui/core/Dialog';
import MuiDialogTitle from '@material-ui/core/DialogTitle';
import MuiDialogContent from '@material-ui/core/DialogContent';
import MuiDialogActions from '@material-ui/core/DialogActions';
import IconButton from '@material-ui/core/IconButton';
import CloseIcon from '@material-ui/icons/Close';
import Typography from '@material-ui/core/Typography';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import movieCard from '../movieCard'
import useBookSearch from './useBookSearch'

export default function Movie() {
    const [page, setPage] = useState(1)
    const [totalPages, setTotalPages] = useState(1);
    const {movies, loading, hasMore} = useBookSearch('fr-FR', page)

    const showItems = () => {
        return movies.map(item => (
            <Grid item xs={4} key={item.id} >
                <Card onClick={this.handleOpenClick}>
                    <CardActionArea>
                        <CardMedia
                            component="img"
                            alt={item.original_title}
                            image={"https://image.tmdb.org/t/p/w500" + item.poster_path}
                            title={item.original_title}
                        />
                        <CardContent >
                            <Typography gutterBottom variant="h5" component="h2">
                                {item.original_title}
                            </Typography>
                            <Typography variant="body2" color="textSecondary" component="p">
                                {"Nombre de visionnage: "}
                                {item.popularity}
                                {" fois"}
                            </Typography>
                        </CardContent>
                    </CardActionArea>
                </Card>
                {}
            </Grid>
        ))
    }


    return (
        <div >
            <Container>
                <Grid>
                    <InfiniteScroll
                        hasMore={hasMore}
                        loader={<div className="loader">{loading && 'Loading...'}</div>}
                        useWindow={false}
                    >
                        {showItems}{" "}
                    </InfiniteScroll>{" "}
                </Grid>
            </Container>
        </div>
    )
}
