import { useEffect, useState } from 'react'
import {request_token} from '../tools/ApiCalls'

export default function useBookSearch(language, pageNumber) {
  const [loading, setLoading] = useState(true)
  const [movies, setMovies] = useState([])
  const [hasMore, setHasMore] = useState(false)

  useEffect(async() => {

    setMovies(await request_token(language, pageNumber))
  }, [pageNumber])

  useEffect(() => {
    setLoading(true)

  }, [language, pageNumber])

  return { loading, movies, hasMore }
}
