import React, { useEffect, useState } from 'react';
import Page from '../Page';
import queryString from 'query-string';
import { confirm_account } from '../tools/ApiCalls';
import Spinner from '../Spinner';
import Alert from '@material-ui/lab/Alert';

export default function AccountConfirmation(props) {
    const params = queryString.parse(props.location.search)
    const token = params.token
    const [spin, setSpin] = useState(false);
    const [response, setResponse] = useState("")

    const DisplayResponse = () => {
        if (response === "")
            return <div/>
        return (response ? 
            <Alert severity="success">Votre compte a bien été validé, vous pouvez maintenant vous connecter</Alert> :
            <Alert severity="error">Une erreur est survenue lors de la confirmation du compte</Alert>
            )
    }

    useEffect(() => {
        async function validateToken() {
            setSpin(true)
            let res = await confirm_account(token)
            setSpin(false)
            setResponse(res.ok)
        }
        validateToken()


      }, [])

    return (
        <Page title="Confirmation du compte">
            {spin && <Spinner />}
            <DisplayResponse />
        </Page>
    );
}
