/*
 *
 *  *  Copyright (C) Thirty One, Inc - All Rights Reserved
 *  *  * Unauthorized copying of this file, via any medium is strictly prohibited
 *  *  * Proprietary and confidential
 *  *  * Written by Thirty One <thirtyoneparis@gmail.com>, 2020
 *  *
 *
 */

import React, { Component, useEffect, useState } from 'react';
import Avatar from '@material-ui/core/Avatar';
import Button from '@material-ui/core/Button';
import LockOutlinedIcon from '@material-ui/icons/LockOutlined';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';
import { Card, CardContent, withStyles, InputAdornment } from '@material-ui/core';
import { Link as RouterLink } from 'react-router-dom';
import { login } from '../tools/ApiCalls'
import { useForm } from 'react-hook-form';
import { Face } from '@material-ui/icons'
import LockIcon from '@material-ui/icons/Lock';
import TextFieldIcon from '../TextFieldIcon';
import Page from '../Page';
import { useStore } from 'react-hookstore';
import AlertMessageError from '../tools/popupError'

const useStyles = makeStyles(theme => ({
  avatar: {
    backgroundColor: theme.palette.secondary.main,
    margin: 'auto',
  },
  form: {
    width: '100%',
    marginTop: theme.spacing(1),
  },
  submit: {
    margin: theme.spacing(3, 0, 2),
  },
  margin: {
    margin: theme.spacing(1),
  },
}));

export default function SignIn(props) {
  const classes = useStyles();
  const { register, handleSubmit, watch, errors } = useForm();
  const [status, setStatusBase] = useState('');
  const [auth, setAuth] = useStore('authStore');
  const onSubmit = async data => {
    let state = await login(data.email, data.password)
    if (state) {
      setAuth(true);
      { !props.noRedirect && props.history.push("/home") };
    } else {
      //[TODO] error messages on no confirm account
      setStatusBase({ msg: "Oops! Une erreur est survenue.Veuillez vérifier votre identifiant et mot de passe.", key: Math.random() });
    }
  }
  useEffect(() => {
	  //console.log(auth)
  });
  const re = /^(([^<>()\[\]\.,;:\s@\"]+(\.[^<>()\[\]\.,;:\s@\"]+)*)|(\".+\"))@(([^<>()[\]\.,;:\s@\"]+\.)+[^<>()[\]\.,;:\s@\"]{2,})$/i;
  return (
    <Page center>
      <div>
        <Container component="main" maxWidth="xs">
          <div>
          <Card>
              <CardContent>
                <Avatar className={classes.avatar}>
                  <LockOutlinedIcon />
                </Avatar>
                <Typography component="h1" variant="h5" style={{ textAlign: 'center' }}>
                  Thirty One
            </Typography>
                <form className={classes.form} noValidate onSubmit={handleSubmit(onSubmit)}>
                    <TextFieldIcon
                      startIcon={<Face />}
                      margin="normal"
                      variant="standard"
                      required
                      fullWidth
                      autoFocus
                      id="email"
                      label="Email"
                      name="email"
                      autoFocus
                      inputRef={register({
                        required: {
                          value: true,
                          message: "This field is required"
                        },
                        pattern: {
                          value: re,
                          message: "Bad email"
                        },
                      })}
                      error={errors.email}
                      helperText={errors.email ? errors.email.message : ''}
                    />
                    <TextFieldIcon
                      startIcon={<LockIcon />}
                      variant="standard"
                      margin="normal"
                      required
                      fullWidth
                      name="password"
                      label="Mot de passe"
                      type="password"
                      error={errors.password}
                      inputRef={register({
                        required: {
                          value: true,
                          message: "This field is required"
                        },
                      })}
                      helperText={errors.password ? errors.password.message : ''}
                    />
                  <Button
                    type="submit"
                    fullWidth
                    variant="contained"
                    color="primary"
                    className={classes.submit}
                  >
                    Connexion
              </Button>
                  {status ? <AlertMessageError key={status.key} message={status.msg} /> : null}

                  <Grid container>
                    <Grid item xs>
                      <Link component={RouterLink} to="/forgot-password" variant="body2">
                        {"Mot de passe oublié?"}
                      </Link>
                    </Grid>
                    <Grid item>
                      <Link component={RouterLink} to="/signup" variant="body2">
                        {"Pas de compte? Inscrivez-vous"}
                      </Link>
                    </Grid>
                  </Grid>
                </form>
              </CardContent>
            </Card>
          </div>
        </Container>
      </div>
    </Page>
  );
}
