import React, { Component, useEffect, useState } from 'react';
import Avatar from '@material-ui/core/Avatar';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import Link from '@material-ui/core/Link';
import Grid from '@material-ui/core/Grid';
import LockOutlinedIcon from '@material-ui/icons/LockOutlined';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';
import { Card, CardContent, withStyles, InputAdornment } from '@material-ui/core';
import { Link as RouterLink } from 'react-router-dom';
import {request_password_reset} from '../tools/ApiCalls'
import { useForm } from 'react-hook-form';
import { Face } from '@material-ui/icons'
import LockIcon from '@material-ui/icons/Lock';
import TextFieldIcon from '../TextFieldIcon';
import Page from '../Page';
import { useStore } from 'react-hookstore';
import AlertMessage from '../tools/popup'
import AlertMessageError from '../tools/popupError'

const useStyles = makeStyles(theme => ({
  avatar: {
    margin: theme.spacing(1),
    backgroundColor: theme.palette.secondary.main,
    margin: 'auto',
  },
  form: {
    width: '100%',
    marginTop: theme.spacing(1),
  },
  submit: {
    margin: theme.spacing(3, 0, 2),
  },
  margin: {
    margin: theme.spacing(1),
  },
}));

export default function ForgotPassword(props) {
  const classes = useStyles();
  const { register, handleSubmit, watch, errors } = useForm();
  const [ auth, setAuth ] = useStore('authStore');
  const onSubmit = async data => {
    let state = await request_password_reset(data.email);
	  if (state.status === 204) {

	  } else {

	  }
  }

  useEffect(() => {
  });
  const re = /^(([^<>()\[\]\.,;:\s@\"]+(\.[^<>()\[\]\.,;:\s@\"]+)*)|(\".+\"))@(([^<>()[\]\.,;:\s@\"]+\.)+[^<>()[\]\.,;:\s@\"]{2,})$/i;
  return (
    <Page center>
    <div>
      <Container component="main" maxWidth="xs"  >
        <div>
          <Card>
            <CardContent>
              <Avatar className={classes.avatar}>
                <LockOutlinedIcon />
              </Avatar>
              <Typography component="h1" variant="h5" style={{ textAlign: 'center' }}>
                Thirty One
              </Typography>

	      <form className={classes.form} onSubmit={handleSubmit(onSubmit)}>
                <Grid container spacing={2}>
                  <Grid item xs={12} sm={6}>
                    <TextFieldIcon
                      startIcon={<Face />}
                      name="firstname"
                      variant="standard"
                      fullWidth
                      label="Prénom *"
                      autoFocus
                      error={errors.firstname}
                      helperText={errors.firstname ? errors.firstname.message : ''}
                      inputRef={register({
                        required: {
                          value: true,
                          message: "Champs requis *"
                        },
                        minLength: {
                          value: 3,
                          message: "Doit contenir plus de 2 caractères"
                        }
                      })}
                    />
                  </Grid>
                  <Grid item xs={12} sm={6}>
                    <TextFieldIcon
                      startIcon={<Face />}
                      variant="standard"
                      fullWidth
                      label="Nom *"
                      name="lastname"
                      inputRef={register({ required: true })}
                      helperText={errors.lastname ? errors.lastname.message : ''}
                      error={errors.lastname}
                    />
                  </Grid>
                  <Grid item xs={12}>

                  </Grid>
                  <Grid item xs={12}>
                    <TextFieldIcon
                      startIcon={<LockIcon />}
                      variant="standard"
                      fullWidth
                      name="password"
                      label="Mot de passe *"
                      type="password"
                      error={errors.password}
                      inputRef={register({
                        required: {
                          value: true,
                          message: "Champs requis *"
			},
			pattern: {
			  value: pass,
            		  message: "Doit avoir 8 caractères, une majuscule, une minuscule et un chiffre"
			}
                      })}
                      helperText={errors.password ? errors.password.message : ''}
                    />
                  </Grid>
		  <Grid item xs={12}>
                    <TextFieldIcon
                      startIcon={<LockIcon />}
                      variant="standard"
                      fullWidth
                      name="confirmPassword"
                      label="Confirmez le mot de passe *"
                      type="password"
                      error={errors.confirmPassword}
                      inputRef={register({
                        required: {
                          value: true,
                          message: "Champs requis *"
                        },
          		pattern: {
            		  value: pass,
            		  message: "Doit avoir 8 caractères, une majuscule, une minuscule et un chiffre"
          		}
                      })}
                      helperText={errors.confirmPassword ? errors.confirmPassword.message : ''}
                    />
                  </Grid>
		</Grid>
		<Button
                  type="submit"
                  fullWidth
                  variant="contained"
                  color="primary"
                  className={classes.submit}
                >
                  S'enregistrer
		</Button>
                <Grid container justify="flex-end">

                </Grid>
              </form>
            </CardContent>
          </Card>
        </div>
      </Container>
      </div>
    </Page>
  );
}
