import React, { useEffect, useState, Component } from 'react';
import { request_token } from '../tools/ApiCalls';
import { makeStyles } from '@material-ui/core/styles';
import InfiniteScroll from "react-infinite-scroller";

class Films extends Component {
    constructor(props) {
        super(props);
        this.state = {
            items: 20,
            hasMoreItems: true,
            data: null,
        };
        console.log()
    }

    async getFilms() {
         this.data = await request_token("fr-FR", 1).results;
        console.log("films", this.data)
    }

    showItems() {
        var items = [];
        for (var i = 0; i < this.state.items; i++) {
            items.push(<li key={i}> Item {i} </li>);
        }
        return items;
    }

    async loadMore() {
        var salr = await this.getFilms()
        if (this.state.items === 200) {

            this.setState({ hasMoreItems: false });
        } else {
            setTimeout(() => {
                this.setState({ items: this.state.items + 20 });
            }, 2000);
        }

    }

    render() {
        return (
            <div>          
                    <InfiniteScroll
                        loadMore={this.loadMore.bind(this)}
                        hasMore={this.state.hasMoreItems}
                        loader={<div className="loader"> Loading... </div>}
                        useWindow={false}
                    >
                        {this.showItems()}{" "}
                    </InfiniteScroll>{" "}
            </div>
        );
    }
}

export default Films;
