 */

import React, { Component, useEffect, useState } from 'react';
import Avatar from '@material-ui/core/Avatar';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import Link from '@material-ui/core/Link';
import Grid from '@material-ui/core/Grid';
import LockOutlinedIcon from '@material-ui/icons/LockOutlined';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';
import { Card, CardContent, withStyles, InputAdornment } from '@material-ui/core';
import { Link as RouterLink } from 'react-router-dom';
import {request_password_reset} from '../tools/ApiCalls'
import { useForm } from 'react-hook-form';
import { Face } from '@material-ui/icons'
import LockIcon from '@material-ui/icons/Lock';
import TextFieldIcon from '../TextFieldIcon';
import Page from '../Page';
import { useStore } from 'react-hookstore';
import AlertMessage from '../tools/popup'
import AlertMessageError from '../tools/popupError'

const useStyles = makeStyles(theme => ({
  avatar: {
    margin: theme.spacing(1),
    backgroundColor: theme.palette.secondary.main,
    margin: 'auto',
  },
  form: {
    width: '100%',
    marginTop: theme.spacing(1),
  },
  submit: {
    margin: theme.spacing(3, 0, 2),
  },
  margin: {
    margin: theme.spacing(1),
  },
}));

export default function ForgotPassword(props) {
  const classes = useStyles();
  const { register, handleSubmit, watch, errors } = useForm();
  const [ status, setStatusBase ] = useState('');
  const [ status2, setStatusBase2 ] = useState('');
  const [ auth, setAuth ] = useStore('authStore');
  const onSubmit = async data => {
    let state = await request_password_reset(data.email);
	  if (state.status === 204) {
	    setStatusBase( { msg: "Un email vous a été envoyé.", key: Math.random() } );
	  } else {
	    setStatusBase2( { msg: "Cet email n'est pas enregistré. Veuillez vérifier votre email.", key: Math.random() } );

	  }
  }

  useEffect(() => {
  });
  const re = /^(([^<>()\[\]\.,;:\s@\"]+(\.[^<>()\[\]\.,;:\s@\"]+)*)|(\".+\"))@(([^<>()[\]\.,;:\s@\"]+\.)+[^<>()[\]\.,;:\s@\"]{2,})$/i;
  return (
    <Page center>
    <div>
      <Container component="main" maxWidth="xs">
        <div>
          >
          <Card>
            <CardContent>
              <Avatar className={classes.avatar}>
                <LockOutlinedIcon />
              </Avatar>
              <Typography component="h1" variant="h5" style={{ textAlign: 'center' }}>
                Thirty One
            </Typography>
              <form className={classes.form} noValidate onSubmit={handleSubmit(onSubmit)}>
                <Grid item md={true} sm={true} xs={true}>
                  <TextFieldIcon
                    startIcon={<Face />}
                    margin="normal"
                    variant="standard"
                    required
                    fullWidth
                    autoFocus
                    id="email"
                    label="Email"
                    name="email"
                    autoFocus
                    inputRef={register({
                      required: {
                        value: true,
                        message: "This field is required"
                      },
                      pattern: {
                        value: re,
                        message: "Bad email"
                      },
                    })}
                    error={errors.email}
                    helperText={errors.email ? errors.email.message : ''}
                  />
                </Grid>
                <Button
                  type="submit"
                  fullWidth
                  variant="contained"
                  color="primary"
                  className={classes.submit}
                >
		  {"Mot de passe oublié"}
              </Button>
	      {status ? <AlertMessage key={status.key} message={status.msg} /> : null}
	      {status2 ? <AlertMessageError key={status2.key} message={status2.msg} /> : null}
              </form>
            </CardContent>
          </Card>
        </div>
      </Container>
    </div>
    </Page>
  );
}
