import React, { useState, useEffect } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Page from '../Page';
import { Grid, Paper, ButtonBase, Button, Avatar, Card, Box, CardContent, CardActions, Icon, CardHeader, IconButton } from '@material-ui/core';
import Typography from '@material-ui/core/Typography';
import Container from '@material-ui/core/Container';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardMedia from '@material-ui/core/CardMedia';
import AccountCircleIcon from '@material-ui/icons/AccountCircle';
import Rating from '@material-ui/lab/Rating';
import Link from '@material-ui/core/Link';
import { Link as RouterLink } from 'react-router-dom';
import SearchBar from 'material-ui-search-bar'
import { api_link } from '../../globals';
import { Carousel } from 'react-responsive-carousel';


import { get_measures, update_user } from '../tools/ApiCalls';

const useStyles = makeStyles(theme => ({
    root: {
        width: 385,
        margin: "auto",
        marginBottom: 25,
        marginLeft: theme.spacing(2)
    },
    media: {
        height: 180,
    },
    removelink: {
        textDecoration: 'none',
    },
    descript: {
        fontWeight: 'bold',
        margin: 'auto',
        marginBottom: 25,
    },
    link: {
        color: theme.palette.secondary.contrastText
    },
	avatar: {
		backgroundColor: 'grey',
	},
}));

export default function StoreCard(props) {
    const classes = useStyles();

    return (
        <Grid
        >
            <Card className={classes.root} key={props.index} >
                <CardActionArea className={classes.area}
                    className={classes.removelink}
                    key={props.index}
                    item
                    component={RouterLink} to={{ pathname: "/store", state: props.item }}
                >
				{/*<CardMedia
                        className={classes.media}
                        image={api_link + '/' + (props.item.pictures.length && props.item.pictures[0].path)}
                        title="User Official"
				/>*/}
				{/*<CardHeader
						className={classes.media}
						avatar={
						  <Avatar aria-label="recipe" className={classes.avatar}>

						  </Avatar>
						}
						title="Commerçants"
						subheader=""
				/>*/}
					<Carousel>

					</Carousel>
                    <CardContent>
                        <Typography gutterBottom variant="h5" component="h2">
                            {props.item.name}
                        </Typography>
                        <Typography variant="body2" color="textSecondary" component="p" className={classes.descript}>
                            {props.item.shortDescription}
                        </Typography>
                        {/*<Rating name="half-rating-read" defaultValue={2.5} value={props.item.notation} readOnly />*/}
                    </CardContent>
                </CardActionArea>

                <a href={(props.item.url.startsWith("http") ? "" : "//") + props.item.url} target="_blank" className={classes.link}>
                    <Box m={2}>Aller vers le site</Box>
                </a>
            </Card>
        </Grid>
    )
};
