 *  *  * Unauthorized copying of this file, via any medium is strictly prohibited
 *  *  * Proprietary and confidential
 *  *  * Written by Thirty One <thirtyoneparis@gmail.com>, 2020
 *  *
 *
 */

import React, { useEffect } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { Grid, Paper, ButtonBase, Button, Avatar, Card, Box, CardContent, Typography, Icon, CardHeader, IconButton } from '@material-ui/core';
import AddCircleOutlineIcon from '@material-ui/icons/AddCircleOutline';
import { display } from '@material-ui/system';
import CreateIcon from '@material-ui/icons/Create';
import Page from '../Page';
import { Link } from 'react-router-dom';
import { get_measures, update_user, get_full_measures } from '../tools/ApiCalls';
import Spinner from '../Spinner';



const useStyles = makeStyles(theme => ({
    root: {
        marginTop: theme.spacing(8),
    },
    paper: {
        height: 200,
        width: 150,
        opacity: 0,
    },
    avatar: {
        width: 100,
        height: 100,
        border: '2px solid #fff',
        background: theme.palette.primary.main,
        color: theme.palette.secondary.main,
    },
    triangle: {
        position: 'absolute',
        height: 0,
        width: 0,
        borderLeft: '120px solid ' + theme.palette.primary.contrastText,
        borderBottom: '120px solid transparent',
        overflow: 'hidden',
        zIndex: -1
    },
    totalHeight: {
        height: "100%",
    },
    add: {
        width: 100,
        height: 100,
        marginTop: theme.spacing(2),
    },
    addCard: {
        background: theme.palette.secondary.main,
        height: 200,
        width: 150,
        opacity: 0,
    },
    modifyIcon: {
        position: 'absolute',
        top: 0,
        right: 0,
        zIndex: 10,
    },
    textCenter: {
        textAlign: 'center',
    },
}));

function UserBox(props) {
    const classes = useStyles()
    console.log(props.item.id);
    console.log("Data measures: ", props.item.data)

    useEffect(() => {
    });
    return (
        <Grid
            key={props.index}
            item
        >
            <ButtonBase style={{ width: '100%' }} component={Link} to={{ pathname: "/showuser", state: props.item }}>

                <Card
                    style={{ animation: "1s ease " + (props.index * 0.01) + "s forwards pop-in" }}
                    className={classes.paper}
                    elevation={3}
                >
                    <Box className={classes.triangle} />

                    <CardHeader />


                    <div>
                        <Grid
                            item
                            align="center"
                        >
                            <Avatar className={classes.avatar} boxShadow={14} />
                        </Grid>
                        <Box p={2} className={classes.textCenter}>
                            <Typography gutterBottom variant="h6" component="h3" color="primary">
                                {props.item.name}
                            </Typography>
                        </Box>
                    </div>

                </Card>
            </ButtonBase>

        </Grid>
    )
}

function AddBox(props) {
    const classes = useStyles()

    return (
        <Grid
            key={props.index}
            item
        >
            <ButtonBase component={Link} to="/adduser">
                <Card
                    style={{ animation: "1s ease " + (props.index * 0.01) + "s forwards pop-in" }}
                    className={classes.addCard}
                    elevation={3}
                >
                    <Grid
                        item
                        align="center"
                    >
                        <AddCircleOutlineIcon fontSize="large" className={classes.add} color="primary" />
                    </Grid>
                    <Box p={1} className={classes.textCenter}>
                        <Typography gutterBottom variant="h6" component="h2" color="primary">
                            Ajouter un profil
                        </Typography>
                    </Box>
                </Card>
            </ButtonBase>
        </Grid>
    )
}

export default function MenuUsers() {
    const classes = useStyles();
    const users = get_measures();

    useEffect(() => {
        console.log(get_measures());
    });

    return (
        <Page center title="Profils">
            <Grid item >
                <Grid
                    container
                    spacing={3}
                    justify="center">
                    {users.map((item, index) => (
                        <UserBox item={item} index={index} />
                    ))}
                    <AddBox index={users.length} />
                </Grid>
            </Grid>
        </Page>
    )
};
