function SimpleMenu(props) {
  const classes = useStyles();
  const [anchorEl, setAnchorEl] = React.useState(null);
  const [deleteOpen, setDeleteOpen] = useState(false);
  const history = useHistory();


  const handleClick = event => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  const handleDeleteClose = () => {
    handleClose();
    setDeleteOpen(false);
  }

  const handleDeleteOpen = () => {
    handleClose();
    setDeleteOpen(true);
  }

  const handleDeleteProfile = () => {
    handleDeleteClose();
    props.callback();
  }

  const handleModify = () => {
    history.push('/modifyuser')
    handleClose();    
  }

  return (
    <div>
      <IconButton
        aria-label="more"
        aria-controls="long-menu"
        aria-haspopup="true"
        className={classes.absoluteIcon}
        onClick={handleClick}
      >
        <MoreVertIcon />
      </IconButton>
      <Menu
        id="simple-menu"
        anchorEl={anchorEl}
        keepMounted
        open={Boolean(anchorEl)}
        onClose={handleClose}
      >
        <MenuItem component={RouterLink} to={{pathname: "/modifyuser", state: props.item}}>Modifier</MenuItem>
        <MenuItem onClick={handleDeleteOpen}>Supprimer</MenuItem>
      </Menu>
      <Dialog
        open={deleteOpen}
        onClose={handleDeleteClose}
        aria-labelledby="alert-dialog-title"
        aria-describedby="alert-dialog-description"
      >
        <DialogTitle id="alert-dialog-title">{"Supprimer le profil " + props.item.name}</DialogTitle>
        <DialogContent>
          <DialogContentText id="alert-dialog-description">
            {"Voulez vous vraiment supprimer le profil de " + props.item.name + "?"}
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleDeleteClose} color="primary">
            Non
          </Button>
          <Button onClick={handleDeleteProfile} className={classes.buttonColor} autoFocus>
            Oui, Supprimer Le Profil
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
}

