import React, { useEffect, useState, Component } from 'react';
import { request_token } from '../tools/ApiCalls';
import { makeStyles } from '@material-ui/core/styles';
import { api_link, api_key } from '../../globals';
import Container from '@material-ui/core/Container';

class Films extends Component {
    constructor(props) {
        super(props);
        this.state = {
            pages: 1,
            films: null,
            loading: false,
            items: 20,
        };
    }
    componentDidMount() {
        // Detect when scrolled to bottom.
        this.refs.myscroll.addEventListener("scroll", () => {
            if (
                this.refs.myscroll.scrollTop + this.refs.myscroll.clientHeight >=
                this.refs.myscroll.scrollHeight
            ) {
                this.loadMore();
            }
        });
    }

    showItems() {
        var items = [];
        this.films = request_token("fr-FR", this.pages).results;
        for (var i = 0; i < this.state.films; i++) {
            items.push(<li key={i}>Item {i}</li>);
        }
        return items;
    }

    loadMore() {
        this.setState({ loading: true });
        setTimeout(() => {
            this.setState({ items: this.state.items + 20, loading: false });
        }, 2000);
    }

    render() {
        return (
            <div className="App">
                <header className="App-header">
                    <img src={logo} className="App-logo" alt="logo" />
                    <h1 className="App-title"> Welcome to React </h1>{" "}
                </header>

                <div style={{ height: '200px', overflow: 'auto' }}>
                    <InfiniteScroll
                        loadMore={this.loadMore.bind(this)}
                        hasMore={this.state.hasMoreItems}
                        loader={<div className="loader"> Loading... </div>}
                        useWindow={false}
                    >
                        {this.showItems()}{" "}
                    </InfiniteScroll>{" "}
                </div>{" "}
            </div>
        );
    }
}

export default Films;
