import React, { useRef, useState, useEffect } from 'react';
import { makeStyles } from '@material-ui/core/styles';

import ExpansionPanel from '@material-ui/core/ExpansionPanel';
import ExpansionPanelDetails from '@material-ui/core/ExpansionPanelDetails';
import ExpansionPanelSummary from '@material-ui/core/ExpansionPanelSummary';
import Typography from '@material-ui/core/Typography';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import DeleteIcon from '@material-ui/icons/Delete';
import StoreIcon from '@material-ui/icons/Store';

/*added*/
import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import Slide from '@material-ui/core/Slide';
import VpnKeyIcon from '@material-ui/icons/VpnKey';
import EmojiEmotionsIcon from '@material-ui/icons/EmojiEmotions';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import List from '@material-ui/core/List';
import Grid from '@material-ui/core/Grid';

import FormControl from '@material-ui/core/FormControl';
import TextField from '@material-ui/core/TextField';
import MailOutlineIcon from '@material-ui/icons/MailOutline';
import WarningIcon from '@material-ui/icons/Warning';

import { useForm } from "react-hook-form";
/*added*/

import Page from '../Page';

import { change_username, change_password, get_user, authorize_merchant, revoke_merchant } from '../tools/ApiCalls';
import Spinner from '../Spinner';
import { useHistory } from 'react-router-dom'

/*added function*/
const Transition = React.forwardRef(function Transition(props, ref) {
  return <Slide direction="up" ref={ref} {...props} />;
});

function ListItemLink(props) {
  return <ListItem button component="a" {...props} />;
}
/*added function*/

  const useStyles = makeStyles(theme => ({
    root: {
      width: '100%',
      margin: theme.spacing(1),
    },
    heading: {
      fontSize: theme.typography.pxToRem(15),
      flexBasis: '33.33%',
      flexShrink: 0,
      fontWeight: "bold"
    },
    secondaryHeading: {
      fontSize: theme.typography.pxToRem(15),
      color: theme.palette.text.secondary,
    },
    buttonColor: {
      color: 'red',
    },
    bodyStyle: {
      maxWidth: 650, 
      margin: 'auto'
    },
    spaceStyle: {
      marginBottom: 25,
    },
    table: {
      minWidth: 650,
    },
    headerIn: {
      fontWeight: "bold"
    }
  }));

  /* functional component */

  const MyMarchanList = () => {
    const classes = useStyles();

    const { handleSubmit, register, errors } = useForm();
    const history = useHistory();

    const [openUp, setOpenUp] = React.useState(false);

    const handleHousOpen = () => {
      setOpenUp(true);
    };
    const handleHousClose = () => {
      setOpenUp(false);
    };

    const onSubmit = async data => {
      /*const [spin, setSpin] = useState(false);
      (true);
      await change_username(data.firstname, data.lastname);
      authorize_merchant
      revoke_merchant
      setSpin(false);
      history.push('/settings')*/
    }

    return (
    <List className={classes.root}>
      {[0, 1, 2, 3].map((value) => {
        const labelId = `checkbox-list-label-${value}`;
      return (
        <ListItem key={value} button onClick={handleHousOpen}>
          <ListItemIcon>
            <DeleteIcon color="primary" className={classes.buttonColor} />
          </ListItemIcon>
          <Typography className={classes.headerIn}>Retirer le marchand n¬∞ {value + 1}</Typography>

        </ListItem>
      );
    })}
    <Dialog
          open={openUp}
          keepMounted
          onClose={handleHousClose}
          aria-labelledby="alert-dialog-title"
          aria-describedby="alert-dialog-description"
          >
            <DialogTitle id="alert-dialog-title"> {"Retirer"}</DialogTitle>
            <DialogContent>
              <DialogContentText id="alert-dialog-description">
              Voulez-vous retirer ce commer√ßant ?
              </DialogContentText>
            </DialogContent>
            <DialogActions>
              <Button onClick={handleHousClose} color="primary">
                Annuler
              </Button>
              <Button onClick={handleHousClose} color="primary" className={classes.buttonColor} autoFocus>
                Retirer ce commer√ßant
              </Button>
            </DialogActions>
          </Dialog>
    </List>
    );
  };

  const UserMyName = () => {
    const classes = useStyles();

    const { handleSubmit, register, errors } = useForm();
    const history = useHistory();

    const [spin, setSpin] = useState(false);
    const stateUser = get_user();
    const onSubmit = async data => {
      setSpin(true);
      await change_username(data.firstname, data.lastname);
      setSpin(false);
      history.push('/settings')
    }

    return <form className={classes.root} noValidate autoComplete="off" onSubmit={handleSubmit(onSubmit)}>
    {spin && <Spinner />}
    <Grid item xs={12}>
      <TextField 
        id="standard-password-input"
        label="Nom"
        fullWidth
        type="text"
        autoComplete="current-password"
        defaultValue={stateUser.firstname} 
        className={classes.spaceStyle}
        name="firstname"
        error={errors.firstname}
        helperText={errors.firstname ? errors.firstname.message : ''}
        inputRef={register({
          required: {
            value: true,
            message: "Champs requis *"
          },
          minLength: {
            value: 3,
            message: "Doit contenir plus de 2 caract√®res"
          }
        })}
      />
    </Grid>
    <Grid item xs={12}>
      <TextField
        id="standard-password-input"
        label="Prenom"
        fullWidth
        type="text"
        autoComplete="current-password"
        defaultValue={stateUser.lastname}  
        className={classes.spaceStyle}
        name="lastname"
        error={errors.lastname}
        helperText={errors.lastname ? errors.lastname.message : ''}
        inputRef={register({
          required: {
            value: true,
            message: "Champs requis *"
          },
          minLength: {
            value: 3,
            message: "Doit contenir plus de 2 caract√®res"
          }
        })}
      />
    </Grid>
    <Button variant="contained" fullWidth color="primary" type="submit">
      Modifier
    </Button>
  </form>;
  };

  const UserMyEmail = () => {
    const classes = useStyles();
    const re = /^(([^<>()\[\]\.,;:\s@\"]+(\.[^<>()\[\]\.,;:\s@\"]+)*)|(\".+\"))@(([^<>()[\]\.,;:\s@\"]+\.)+[^<>()[\]\.,;:\s@\"]{2,})$/i;


    const { handleSubmit, register, errors } = useForm();
    const history = useHistory();
    const stateUser = get_user();
    const [spin, setSpin] = useState(false);
    const onSubmit = async data => {
      setSpin(true);
      //await change_email(data.email);
      setSpin(false);
      history.push('/settings')
    }

    return <form className={classes.root} noValidate autoComplete="off" onSubmit={handleSubmit(onSubmit)}>
    {spin && <Spinner />}
    <Grid item xs={12}>
      <TextField
        id="standard-password-input"
        label="Adresse mail"
        type="email"
        fullWidth
        autoComplete="current-password"
        defaultValue={stateUser.email} 
        className={classes.spaceStyle}
        name="email"
        inputRef={register({
          required: {
            value: true,
            message: "Champs requis *"
          },
          pattern: {
            value: re,
            message: "Mauvais email"
          },
        })}
        error={errors.email}
        helperText={errors.email ? errors.email.message : ''}
      />
    </Grid>
    <Button variant="contained" fullWidth color="primary" type="submit">
      Modifier
    </Button>
  </form>;
  };

  const UserMyPassword = () => {
    const classes = useStyles();

    const pass = /^(?=.*\d)(?=.*[a-z])(?=.*[A-Z])[0-9a-zA-Z]{8,}$/;

    const { handleSubmit, register, errors } = useForm();
    const history = useHistory();

    const [spin, setSpin] = useState(false);
    const onSubmit = async data => {
      setSpin(true);
      await change_password(data.oldpassword, data.newpassword);
      setSpin(false);
      history.push('/settings')
    }

    return <form className={classes.root} noValidate autoComplete="off"  onSubmit={handleSubmit(onSubmit)}>
    {spin && <Spinner />}
    <Grid item xs={12}>
      <TextField
        id="standard-password-input"
        label="Ancien mot de passe"
        type="password"
        fullWidth
        autoComplete="current-password"
        defaultValue="" 
        className={classes.spaceStyle}
        name="oldpassword"
        error={errors.oldpassword}
        inputRef={register({
          required: {
            value: true,
            message: "Champs requis *"
          },
          pattern: {
            value: pass,
            message: "Doit avoir 8 caract√®res, une majuscule, une minuscule et un chiffre"
          }
        })}
        helperText={errors.oldpassword ? errors.oldpassword.message : ''}
      />
    </Grid>
    <Grid item xs={12}>
      <TextField
        id="standard-password-input"
        label="Nouveau mot de passe"
        type="password"
        fullWidth
        autoComplete="current-password"
        defaultValue=""
        className={classes.spaceStyle}
        name="newpassword"
        error={errors.newpassword}
        inputRef={register({
          required: {
            value: true,
            message: "Champs requis *"
          },
          pattern: {
            value: pass,
            message: "Doit avoir 8 caract√®res, une majuscule, une minuscule et un chiffre"
          }
        })}
        helperText={errors.newpassword ? errors.newpassword.message : ''}
      />
    </Grid>
    <Button variant="contained" fullWidth color="primary" type="submit">
      Changer
    </Button>
    </form>;
  };
  /* end functionnal component */


export default function Settings() {
    const classes = useStyles();

    const [expanded, setExpanded] = React.useState(false);

    const handleChange = panel => (event, isExpanded) => {
      setExpanded(isExpanded ? panel : false);
    };

    const [open, setOpen] = React.useState(false);

    const handleClickOpen = () => {
      setOpen(true);
    };
    const handleClose = () => {
      setOpen(false);
    };

    /* add event effect on parametre */
    const ref = useRef();
    const ClickOut = (ref, callback) => {
      const handleClick = e => {
        if (ref.current && !ref.current.contains(e.target)) {
          callback();
        }
      };

      useEffect(() => {
        document.addEventListener("click", handleClick);

        return () => {
          document.removeEventListener("click", handleClick);
        };
      });
    };

    ClickOut(ref, () => {
      setExpanded(false);
    });
    /* add event effect on parametre */ 

    const { handleSubmit, register, errors } = useForm();
    const onSubmit = values => {
      console.log(values);
    };

    return (
      <Page title="Param√®tres">
        <div>
            <div className={classes.root} className={classes.bodyStyle} ref={ref}>

              <ExpansionPanel>
                <ExpansionPanelSummary
                  expandIcon={<ExpandMoreIcon />}
                  aria-controls="panel1a-content"
                  id="panel1a-header"
                >
                  <ListItemIcon>
                    <StoreIcon />
                  </ListItemIcon>
                  <Typography className={classes.heading}>Mes Marchands</Typography>
                </ExpansionPanelSummary>
                <ExpansionPanelDetails>
                  <Typography>
                  <Grid container spacing={2}>
                    <MyMarchanList />
                  </Grid>
                  </Typography>
                </ExpansionPanelDetails>
              </ExpansionPanel>  
              <br/>
              <ExpansionPanel expanded={expanded === 'panel1'} onChange={handleChange('panel1')} >
                <ExpansionPanelSummary
                  expandIcon={<ExpandMoreIcon />}
                  aria-controls="panel1bh-content"
                  id="panel1bh-header"
                >
                  <ListItemIcon>
                    <EmojiEmotionsIcon />
                  </ListItemIcon>
                  <Typography className={classes.heading}>Changer mon nom</Typography>
                </ExpansionPanelSummary>
                <ExpansionPanelDetails>
                  <Typography>
                    <Grid container spacing={2}>
                      <UserMyName />  
                    </Grid>
                  </Typography>
                </ExpansionPanelDetails>
              </ExpansionPanel>
              <ExpansionPanel expanded={expanded === 'panel2'} onChange={handleChange('panel2')}>
                  <ExpansionPanelSummary
                    expandIcon={<ExpandMoreIcon />}
                    aria-controls="panel2bh-content"
                    id="panel2bh-header"
                  >
                  <ListItemIcon>
                    <MailOutlineIcon />
                  </ListItemIcon>

                  <Typography className={classes.heading}>Changer mon email</Typography>
                  </ExpansionPanelSummary>
                <ExpansionPanelDetails>
                  <Typography>
                    <Grid container spacing={1}>
                      <UserMyEmail />
                    </Grid>
                  </Typography>
                </ExpansionPanelDetails>

              </ExpansionPanel>

              <ExpansionPanel expanded={expanded === 'panel3'} onChange={handleChange('panel3')}>
                  <ExpansionPanelSummary
                    expandIcon={<ExpandMoreIcon />}
                    aria-controls="panel3bh-content"
                    id="panel3bh-header"
                  >
                  <ListItemIcon>
                    <VpnKeyIcon />
                  </ListItemIcon>
                  <Typography className={classes.heading}>Changer mon mot de passe</Typography>
                  </ExpansionPanelSummary>
                  <ExpansionPanelDetails>
                    <Typography>
                      <Grid container spacing={2}>
                        <UserMyPassword/>
                      </Grid>
                    </Typography>
                  </ExpansionPanelDetails>
              </ExpansionPanel>
              <br/>
              <ExpansionPanel expanded={expanded === 'panel4'} onClick={handleClickOpen}>
                <ExpansionPanelSummary
                    aria-controls="panel4bh-content"
                    id="panel4bh-header"
                    className={classes.buttonColor}
                  >
                    <ListItemIcon>
                      <DeleteIcon color="primary" className={classes.buttonColor} />
                    </ListItemIcon>
                    <Typography className={classes.heading}>Supprimer mon compte</Typography>
                  </ExpansionPanelSummary>
              </ExpansionPanel>

              <Dialog
              open={open}
              keepMounted
              onClose={handleClose}
              aria-labelledby="alert-dialog-title"
              aria-describedby="alert-dialog-description"
              >
                <DialogTitle id="alert-dialog-title"> {"Supprimer mon compte"}</DialogTitle>
                <DialogContent>
                  <DialogContentText id="alert-dialog-description">
                  Voulez-vous supprimer ce compte ?
                  </DialogContentText>
                </DialogContent>
                <DialogActions>
                  <Button onClick={handleClose} color="primary">
                    Annuler
                  </Button>
                  <Button onClick={handleClose} color="primary" className={classes.buttonColor} autoFocus>
                    Supprimer mon compte
                  </Button>
                </DialogActions>
              </Dialog>
          </div>
        </div>
      </Page>
      );
};
