import React, { useEffect, useState, Component } from 'react';
import { request_token, request_pages } from '../tools/ApiCalls';
import { makeStyles } from '@material-ui/core/styles';
import InfiniteScroll from "react-infinite-scroller";
import Container from '@material-ui/core/Container';

const useStyles = makeStyles((theme) => ({
	root: {

	},
	paper: {
		padding: theme.spacing(2),
		textAlign: 'center',
		color: theme.palette.text.secondary,
	},
	larger: {
		width: "100%",
		opacity: 0.7,
	},
	paperthree: {
		textAlign: 'center',
		backgroundSize: 'cover',
		backgroundRepeat: 'no-repeat',
		backgroundPosition: 'center',
		width: "calc(100% + 12px)",
		left: 0,
		top: 64,
		position: "absolute",
	},

}));

const defaultProps = {
	bgcolor: 'background.paper',
	m: 1,
	style: { width: '5rem', height: '5rem' },
	borderColor: 'text.primary',
};

class Films extends Component {
    constructor(props) {
        super(props);
        this.state = {
            data: [],
            hasMoreItems: true,
            totalPages: 0,
            pages: 1,
            language: "fr-FR",
            all: []
        };
    }
    // async componentDidMount() {
    // const response = await request_token("fr-FR", 1);
    // this.setState({ data: response });
    // console.log('data', response)
    // }
    // 
    showItems() {
        return this.state.data.map(item => (
            <div key={item.id}>{item.title}</div>
        ))
    }

    async getTotalPages() {
        this.state.totalPages = await request_pages();
    }

    async loadMore() {
        if (this.state.pages === 3 /*this.state.totalPages*/) {
            this.setState({ hasMoreItems: false });
        } else {
            const response = await request_token("fr-FR", this.state.pages);
            setTimeout(() => {
                const add = this.state.data;
                const to_add = add.concat(response);
                this.setState({ data: to_add })
            }, 2000);
        }
        this.state.pages += 1
    }

    render() {
        return (
            <div
                loader={this.getTotalPages()}
            >
                <InfiniteScroll
                    loadMore={this.loadMore.bind(this)}
                    hasMore={this.state.hasMoreItems}
                    loader={<div className="loader"> Loading... </div>}
                    useWindow={false}
                >
                    {this.showItems()}{" "}
                </InfiniteScroll>{" "}
            </div>
        );
    }
}

export default Films;
