import React from 'react';
import PropTypes from 'prop-types';
import { makeStyles, withStyles, darken } from '@material-ui/core/styles';
import clsx from 'clsx';
import Stepper from '@material-ui/core/Stepper';
import Step from '@material-ui/core/Step';
import StepLabel from '@material-ui/core/StepLabel';
import Check from '@material-ui/icons/Check';
import StepConnector from '@material-ui/core/StepConnector';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import { theme } from '../../Theme';
import { Card, TextField, Grid, Container } from '@material-ui/core';
import StoreIcon from '@material-ui/icons/Store';
import InfoIcon from '@material-ui/icons/Info';
import SettingsIcon from '@material-ui/icons/Settings';
import ImageUpload from '../ImageUploader'
import TextFieldIcon from '../TextFieldIcon';

import StoreMallDirectoryIcon from '@material-ui/icons/StoreMallDirectory';
import LanguageIcon from '@material-ui/icons/Language';
import { MenuBook, LocationOn, Face } from '@material-ui/icons';
import LockIcon from '@material-ui/icons/Lock';



const ColorlibConnector = withStyles(theme => ({
  alternativeLabel: {
    top: 22,
  },
  active: {
    '& $line': {
      background: theme.palette.secondary.main,
    },
  },
  completed: {
    '& $line': {
      background: theme.palette.secondary.main,
    },
  },
  line: {
    height: 3,
    border: 0,
    backgroundColor: theme.palette.primary.dark,
    borderRadius: 1,
  },
}))(StepConnector);

const useColorlibStepIconStyles = makeStyles(theme => ({
  root: {
    backgroundColor: theme.palette.primary.dark,
    zIndex: 1,
    color: theme.palette.primary.main,
    width: 50,
    height: 50,
    display: 'flex',
    borderRadius: '50%',
    justifyContent: 'center',
    alignItems: 'center',
  },
  active: {
    background: theme.palette.secondary.main,
    boxShadow: '0 4px 10px 0 rgba(0,0,0,.25)',
  },
  completed: {
    background: theme.palette.secondary.main,
  },
}));

function ColorlibStepIcon(props) {
  const classes = useColorlibStepIconStyles();
  const { active, completed } = props;

  const icons = {
    1: <StoreIcon />,
    2: <InfoIcon />,
    3: <SettingsIcon />,
  };

  return (
    <div
      className={clsx(classes.root, {
        [classes.active]: active,
        [classes.completed]: completed,
      })}
    >
      {icons[String(props.icon)]}
    </div>
  );
}

ColorlibStepIcon.propTypes = {
  active: PropTypes.bool,
  completed: PropTypes.bool,
  icon: PropTypes.node,
};

const useStyles = makeStyles(theme => ({
  root: {
    width: '100%',
  },
  button: {
    marginRight: theme.spacing(1),
  },
  instructions: {
    marginTop: theme.spacing(1),
    marginBottom: theme.spacing(1),
    textAlign: 'center',
  },
  step: {
    color: theme.palette.secondary.main,
  },
  card: {
    padding: theme.spacing(2),
  },
  buttons: {
    textAlign: 'right',
    marginTop: theme.spacing(5)
  },
}));

function getSteps() {
  return ['Votre Magasin', 'Vos Informations', 'Votre Compte'];
}

function getStepContent(step) {
  switch (step) {
    case 0:
      return 'Votre Magasin';
    case 1:
      return 'Vos Informations';
    case 2:
      return 'Votre Compte';
    default:
      return 'Unknown step';
  }
}

export default function SellerRegistration() {
  const classes = useStyles();
  const [activeStep, setActiveStep] = React.useState(0);
  const steps = getSteps();

  const handleNext = () => {
    setActiveStep(prevActiveStep => prevActiveStep + 1);
  };

  const handleBack = () => {
    setActiveStep(prevActiveStep => prevActiveStep - 1);
  };

  const handleReset = () => {
    setActiveStep(0);
  };

  return (
    <div className={classes.root}>
      <Stepper alternativeLabel activeStep={activeStep} connector={<ColorlibConnector />} style={{ backgroundColor: "transparent" }}>
        {steps.map(label => (
          <Step key={label} className={classes.step}>
            <StepLabel StepIconComponent={ColorlibStepIcon} className={classes.step}>{label}</StepLabel>
          </Step>
        ))}
      </Stepper>
      <Card className={classes.card}>
        {activeStep === steps.length ? (
          <div>
            <Typography className={classes.instructions}>
              Votre inscription a bien été prise en compte! Vous recevrez une réponse par e-mail d'ici 24h!
            </Typography>
          </div>
        ) : (
            <div>
              <Typography className={classes.instructions} variant="h6">{getStepContent(activeStep)}</Typography>
              {getStepForm(activeStep)}
              <div className={classes.buttons}>
                <Button disabled={activeStep === 0} onClick={handleBack} className={classes.button}>
                  Précédent
                </Button>
                <Button
                  variant="contained"
                  color="primary"
                  onClick={handleNext}
                  className={classes.button}
                >
                  {activeStep === steps.length - 1 ? 'Terminer' : 'Suivant'}
                </Button>
              </div>
            </div>
          )}
      </Card>
    </div>
  );
}

function StoreForm() {
  return (
    <Container component="main" maxWidth="xs">
      <TextFieldIcon
        fullWidth
        label="Nom Du Magasin"
        margin="normal"
        variant="standard"
        startIcon={<StoreIcon/>}
      />
      <TextFieldIcon
        fullWidth
        label="URL Du Site E-Commerce"
        margin="normal"
        variant="standard"
        startIcon={<LanguageIcon/>}
      />
      <TextFieldIcon
        fullWidth
        label="N° De Siret"
        margin="normal"
        variant="standard"
        startIcon={<MenuBook/>}
      />
      <TextFieldIcon
        fullWidth
        label="Adresse"
        margin="normal"
        variant="standard"
        startIcon={<LocationOn/>}
      />
      <TextFieldIcon
        fullWidth
        label="Code Postal"
        margin="normal"
        variant="standard"
        startIcon={<LocationOn/>}
      />
      <ImageUpload />
    </Container>
  )
}

function InfoForm() {
  return (
    <Container component="main" maxWidth="xs">
      <TextFieldIcon
        margin="normal"
        variant="outlined"
        label="Description Succinte de ce que vous proposez"
        multiline
        fullWidth
        rows={5}
        rowsMax={5}
      />

    </Container>
  )
}

function AccountForm() {
  return (
    <Container component="main" maxWidth="xs">
      <TextFieldIcon
        fullWidth
        label="E-Mail"
        margin="normal"
        variant="standard"
        startIcon={<Face/>}
      />
      <TextFieldIcon
        fullWidth
        label="Mot De Passe"
        name="password"
        type="password"
        margin="normal"
        variant="standard"
        startIcon={<LockIcon/>}
      />
      <TextFieldIcon
        fullWidth
        type="password"
        label="Confirmation Du Mot De Passe"
        name="password"
        margin="normal"
        variant="standard"
        startIcon={<LockIcon/>}
      />
    </Container>
  )
}

function getStepForm(step) {
  switch (step) {
    case 0:
      return <StoreForm />;
    case 1:
      return <InfoForm />;
    case 2:
      return <AccountForm />;
    default:
      return 'Unknown step';
  }
}
