import { useEffect, useState } from 'react'
import {request_token} from '../tools/ApiCalls'

export default function useBookSearch(movies, language, pageNumber) {
  const [loading, setLoading] = useState(true)
  const [newMovies, setNewMovies] = useState([])
  const [hasMore, setHasMore] = useState(false)

  useEffect(async() => {
    setNewMovies(movies);
    const tmp = await request_token(language, pageNumber)
    setNewMovies(movies.concat(tmp))
  }, [])

  useEffect(() => {
    setLoading(true)

  }, [])

  return { loading, newMovies, hasMore }
}
