import React, { Component } from 'react';
import { request_token, request_pages } from '../tools/ApiCalls';
import { makeStyles } from '@material-ui/core/styles';
import { withStyles } from '@material-ui/core/styles';
import InfiniteScroll from "react-infinite-scroller";
import Container from '@material-ui/core/Container';
import Spinner from '../Spinner'
import Grid from '@material-ui/core/Grid';
import Dialog from '@material-ui/core/Dialog';
import MuiDialogTitle from '@material-ui/core/DialogTitle';
import MuiDialogContent from '@material-ui/core/DialogContent';
import MuiDialogActions from '@material-ui/core/DialogActions';
import IconButton from '@material-ui/core/IconButton';
import CloseIcon from '@material-ui/icons/Close';
import Typography from '@material-ui/core/Typography';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import movieCard from '../movieCard'


const useStyles = makeStyles((theme) => ({
    root: {

    },
}));

const defaultProps = {
    bgcolor: 'background.paper',
    m: 1,
    style: { width: '5rem', height: '5rem' },
    borderColor: 'text.primary',
};

export default function Movie() {
    const classes = useStyles();
    let datas = {
        movies: [],
        hasMoreItems: true,
        totalPages: 0,
        pages: 1,
        language: "fr-FR",
    }
    function showItems() {
        return datas.movies.map(item => (
            <Grid item xs={4} key={item.id} >
                <Card onClick={this.handleOpenClick}>
                    <CardActionArea>
                        <CardMedia
                            component="img"
                            alt={item.original_title}
                            image={"https://image.tmdb.org/t/p/w500" + item.poster_path}
                            title={item.original_title}

                        />
                        <CardContent >
                            <Typography gutterBottom variant="h5" component="h2">
                                {item.original_title}
                            </Typography>
                            <Typography variant="body2" color="textSecondary" component="p">
                                {"Nombre de visionnage: "}
                                {item.popularity}
                                {" fois"}
                            </Typography>
                        </CardContent>
                    </CardActionArea>
                </Card>
            </Grid>

        ))
    }

    async function getTotalPages() {
        datas.totalPages = await request_pages();
    }

    async function loadMore() {
        if (datas.pages === 2 /*this.state.totalPages*/) {
             datas.hasMoreItems = false;
        } else {
            const response = await request_token("fr-FR", datas.pages);
            setTimeout(() => {
                const add = datas.movies;
                const to_add = add.concat(response);
                datas.movies = to_add
            }, 2000);
        }
        datas.pages += 1
    }

    return (
        <div className={classes.root}>
            <Container>
                <Grid>
                    <InfiniteScroll
                        loadMore={loadMore()}
                        hasMore={datas.hasMoreItems}
                        loader={<div className="loader"> Loading... </div>}
                        useWindow={false}
                    >
                        {showItems()}{" "}
                    </InfiniteScroll>{" "}
                </Grid>
            </Container>
        </div>
    );
}
