import React, { Component } from 'react';
import { request_token, request_pages } from '../tools/ApiCalls';
import { makeStyles } from '@material-ui/core/styles';
import { withStyles } from '@material-ui/core/styles';
import InfiniteScroll from "react-infinite-scroller";
import Container from '@material-ui/core/Container';
import Spinner from '../Spinner'
import Grid from '@material-ui/core/Grid';
import Dialog from '@material-ui/core/Dialog';
import MuiDialogTitle from '@material-ui/core/DialogTitle';
import MuiDialogContent from '@material-ui/core/DialogContent';
import MuiDialogActions from '@material-ui/core/DialogActions';
import IconButton from '@material-ui/core/IconButton';
import CloseIcon from '@material-ui/icons/Close';
import Typography from '@material-ui/core/Typography';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';

const useStyles = makeStyles((theme) => ({
    root: {
        margin: 0,
        padding: theme.spacing(2),
      },
      closeButton: {
        position: 'absolute',
        right: theme.spacing(1),
        top: theme.spacing(1),
        color: theme.palette.grey[500],
      },
}));

const DialogTitle = withStyles(styles)((props) => {
    const { children, classes, onClose, ...other } = props;
    return (
      <MuiDialogTitle disableTypography className={classes.root} {...other}>
        <Typography variant="h6">{children}</Typography>
        {onClose ? (
          <IconButton aria-label="close" className={classes.closeButton} onClick={onClose}>
            <CloseIcon />
          </IconButton>
        ) : null}
      </MuiDialogTitle>
    );
  });


class Films extends Component {
    constructor(props) {
        super(props);
        this.state = {
            data: [],
            hasMoreItems: true,
            totalPages: 0,
            pages: 1,
            language: "fr-FR",
            all: []
        };
    }

    showItems() {
        return this.state.data.map(item => (
            <Grid item xs={4} key={item.id} >
                <Card >
                    <CardActionArea>
                        <CardMedia
                            component="img"
                            alt={item.original_title}
                            image={"https://image.tmdb.org/t/p/w500" + item.poster_path}
                            title={item.original_title}

                        />
                        <CardContent >
                            <Typography gutterBottom variant="h5" component="h2">
                                {item.original_title}
                            </Typography>
                            <Typography variant="body2" color="textSecondary" component="p">
                                {"Nombre de visionnage: "}
                                {item.popularity}
                                {" fois"}
                            </Typography>
                        </CardContent>
                    </CardActionArea>
                </Card>
            </Grid>

        ))
    }

    async getTotalPages() {
        this.state.totalPages = await request_pages();
    }

    async loadMore() {
        if (this.state.pages === 2 /*this.state.totalPages*/) {
            this.setState({ hasMoreItems: false });
        } else {
            const response = await request_token("fr-FR", this.state.pages);
            setTimeout(() => {
                const add = this.state.data;
                const to_add = add.concat(response);
                this.setState({ data: to_add })
            }, 2000);
        }
        this.state.pages += 1
    }

    render() {
        return (

            <div
                loader={this.getTotalPages()}
            >
                <Container>
                    <InfiniteScroll
                        loadMore={this.loadMore.bind(this)}
                        hasMore={this.state.hasMoreItems}
                        loader={<div className="loader"> Loading... </div>}
                        useWindow={false}
                    >
                        {this.showItems()}{" "}
                    </InfiniteScroll>{" "}
                </Container>
            </div>
        );
    }
}

export default Films;
