import React, { useEffect, useState, Component } from 'react';
import { request_token, request_pages } from '../tools/ApiCalls';
import { makeStyles } from '@material-ui/core/styles';
import InfiniteScroll from "react-infinite-scroller";

class Films extends Component {
    constructor(props) {
        super(props);
        this.state = {
            data: [],
            items: 20,
            hasMoreItems: true,
            totalPages: this.getTotalPages(),
        };
    }
    async componentDidMount() {
        const response = await request_token("fr-FR", 1);
        this.setState({ data: response });
        console.log('data', response)
    }

    showItems() {
        return this.state.data.map(item => (
            <div key={item.id}>{item.title}</div>
        ))
    }

    async getTotalPages() {
        this.state.totalPages = await request_pages();
    }

    async loadMore() {
        if(this.state.items === this.state.totalPages ){

          this.setState({ hasMoreItems: false});
        }else{
            setTimeout(() => {
            this.setState({ items: this.state.items + 20});
        }, 2000);
        }

      }

    render() {
        return (        
            <div>
                <InfiniteScroll
                    loader={<div className="loader"> Loading... </div>}
                    useWindow={false}
                >
                    {this.showItems()}{" "}
                </InfiniteScroll>{" "}
            </div>
        );
    }
}

export default Films;
