 *  *
 *
 */

import React, { useEffect, useState } from 'react';
import Page from '../Page';
import { Card, makeStyles, Button, Box } from '@material-ui/core';
import SignIn from './SignIn';
import { useStore } from 'react-hookstore';
import queryString from 'query-string';
import Logo from '../Logo';
import LockIcon from '@material-ui/icons/Lock';
import { authorize_merchant, revoke_merchant, get_merchants, get_merchant } from '../tools/ApiCalls';
import { api_link } from '../../globals';

const useStyles = makeStyles(theme => ({
    root: {
        width: '100%',
    },
    button: {
        marginRight: theme.spacing(1),
    },
    instructions: {
        marginTop: theme.spacing(1),
        marginBottom: theme.spacing(1),
        textAlign: 'center',
    },
    image: {
        margin: '0 auto',
        width: 200,
        height: 200,
        display: 'block',
    },
    card: {
        padding: theme.spacing(2),
    },
    buttons: {
        textAlign: 'right',
        marginTop: theme.spacing(5),
    },
    icon: {
        color: theme.palette.primary.main
    }
}));

export default function Approve(props) {
    const classes = useStyles();
    const [auth, setAuth] = useStore('authStore');
    const [merchant, setMerchant] = useState({})
    const [picture, setPicture] = useState("")
    const [name, setName] = useState("")
    const params = queryString.parse(props.location.search)
    console.log(params)

    useEffect(() => {
        const fetchData = async () => {
            const result = await get_merchants()
            result.merchants.map((merchant, key) => {
                if (merchant.id == params.merchantId) {
                    setMerchant(merchant)
                    setName(merchant.name)
                    merchant.pictures.length && setPicture(api_link + "/" + merchant.pictures[0].path)
                    console.log(merchant.pictures)
                    console.log(merchant)
                }
            })
        };
        fetchData();
    }, [])

    const accept = async () => {
        await authorize_merchant(params.merchantId); // Let's assume that the parameter handled is named merchantId
        window.close();
    }

    const refuse = () => {
        window.close();
    }

    return (
        auth ?
            <Page center >
                <Logo />
                <Card className={classes.card}>

                <img src={picture} alt="merchant_picture" className={classes.image} />

                    <Box m={2}>
                    {name} recevra les informations concernants vos profils de mesures
                    </Box>
                    <Box m={2}>
                       <LockIcon className={classes.icon} /> Cela ne donne pas acc√®s aux informations personnelles de votre compte
                    </Box>
                        <div className={classes.buttons}>
                        <Button className={classes.button} onClick={refuse}>
                            Refuser
                </Button>
                        <Button
                        onClick={accept}
                            variant="contained"
                            color="primary"
                        >
                            Accepter
                        </Button>
                    </div>

                </Card>
            </Page> :
            <SignIn noRedirect />
    );
}
