import React, { useEffect, useState, Component } from 'react';
import { request_token, request_pages } from '../tools/ApiCalls';
import { makeStyles } from '@material-ui/core/styles';
import InfiniteScroll from "react-infinite-scroller";

class Films extends Component {
    constructor(props) {
        super(props);
        this.state = {
            data: [],
            hasMoreItems: true,
            totalPages: 0,
            pages: 1,
            language: "fr-FR"
        };
    }
    async componentDidMount() {
        const response = await request_token("fr-FR", 1);
        this.setState({ data: response });
        console.log('data', response)
    }

    showItems() {
        return this.state.data.map(item => (
            <div key={item.id}>{item.title}</div>
        ))
    }

    async getTotalPages() {
        this.state.totalPages = await request_pages();
    }

    async loadMore() {
        if (this.state.pages === this.state.totalPages) {
            this.setState({ hasMoreItems: false });
        } else {
            this.state.pages += 1
            const response = await request_token("fr-FR", this.state.pages);
            this.setState({ data: response });
        }

    }

    render() {
        return (
            <div
                loader={this.getTotalPages()}
            >

                <InfiniteScroll
                 loadMore={this.loadMore.bind(this)}
                 hasMore={this.state.hasMoreItems}
                    loader={<div className="loader"> Loading... </div>}
                    useWindow={false}
                >
                    {this.showItems()}{" "}
                </InfiniteScroll>{" "}
            </div>
        );
    }
}

export default Films;
