import useBookSearch from './useBookSearch'
import React, { Component, useState } from 'react';
import { request_token, request_pages } from '../tools/ApiCalls';
import { makeStyles } from '@material-ui/core/styles';
import { withStyles } from '@material-ui/core/styles';
import InfiniteScroll from "react-infinite-scroller";
import Container from '@material-ui/core/Container';
import Spinner from '../Spinner'
import Grid from '@material-ui/core/Grid';
import Dialog from '@material-ui/core/Dialog';
import MuiDialogTitle from '@material-ui/core/DialogTitle';
import MuiDialogContent from '@material-ui/core/DialogContent';
import MuiDialogActions from '@material-ui/core/DialogActions';
import IconButton from '@material-ui/core/IconButton';
import CloseIcon from '@material-ui/icons/Close';
import Typography from '@material-ui/core/Typography';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import movieCard from '../movieCard'

export default function App() {
    const [query, setQuery] = useState('')
    const [page, setPage] = useState(1)
    const [totalPages,setTotalPages] = 0;

    const {
        movies,
        hasMore,
        loading,
    } = useBookSearch("fr-FR", page)


    function showItems() {
        return movies.map(item => (
            <Grid item xs={4} key={item.id} >
                <Card onClick={this.handleOpenClick}>
                    <CardActionArea>
                        <CardMedia
                            component="img"
                            alt={item.original_title}
                            image={"https://image.tmdb.org/t/p/w500" + item.poster_path}
                            title={item.original_title}

                        />
                        <CardContent >
                            <Typography gutterBottom variant="h5" component="h2">
                                {item.original_title}
                            </Typography>
                            <Typography variant="body2" color="textSecondary" component="p">
                                {"Nombre de visionnage: "}
                                {item.popularity}
                                {" fois"}
                            </Typography>
                        </CardContent>
                    </CardActionArea>
                </Card>
            </Grid>

        ))
    }

    async function getTotalPages() {
        setTotalPages(await request_pages());
    }

    async function loadMore() {
        if (page === 2 /*this.state.totalPages*/) {
            this.setState({ hasMoreItems: false });
        } else {
            const response = await request_token("fr-FR", this.state.pages);
            setTimeout(() => {
                const add = this.state.data;
                const to_add = add.concat(response);
                this.setState({ data: to_add })
            }, 2000);
        }
        setPage(page + 1)
    }

    return (
        <div
        loader={getTotalPages()}
    >
        <Container>
            <Grid>
            <InfiniteScroll
                loadMore={loadMore.bind()}
                hasMore={hasMore}
                loader={<div className="loader">{loading && 'Loading...'}</div>}
                useWindow={false}
            >
                {showItems()}{" "}
            </InfiniteScroll>{" "}
            </Grid>
        </Container>
    </div>
    )
}
