import { useEffect, useState } from 'react'
import {request_token} from '../tools/ApiCalls'

export default function useBookSearch(language, pageNumber, totalPages) {
  const [loading, setLoading] = useState(true)
  const [newMovies, setNewMovies] = useState([])
  const [hasMore, setHasMore] = useState(true)

  useEffect(async() => {
    setLoading(true)
    setNewMovies(newMovies);
    const tmp = await request_token(language, pageNumber)
    setNewMovies(newMovies.concat(tmp))
    console.log('tmp', tmp)
  }, [pageNumber < totalPages])

  useEffect(() => {
    setLoading(false)

  }, [])

  return {newMovies, loading, hasMore }
}
