import { useEffect, useState } from 'react'
import {request_token} from '../tools/ApiCalls'

export default function useBookSearch(language) {
  const [loading, setLoading] = useState(true)
  const [newMovies, setNewMovies] = useState([])
  const [hasMore, setHasMore] = useState(false)

  useEffect(async() => {
    setLoading(true)
    setNewMovies(newMovies);
    const tmp = await request_token(language, pageNumber)
    setNewMovies(newMovies.concat(tmp))
    console.log('tmp', tmp)
  }, [pageNumber])

  useEffect(() => {
    setLoading(false)

  }, [pageNumber])

  return { loading, newMovies, hasMore }
}
